year = 1000year_2 = 2022running = Truewhile running:    year_of_birth = int(input('Введите год рождения '))    if year_of_birth >= year:        year_of_life = year_2 - year_of_birth        if year_of_life <= 3:            print(year_of_life, 'Малыш')            break        if year_of_life <= 9:            print(year_of_life, 'Малыш')            break        if year_of_life <= 15:            print(year_of_life, 'Подросток')            break        if year_of_life <= 18:            print(year_of_life, 'Молодой')            break        if year_of_life <= 50:            print(year_of_life, 'Взрослый')            break        if year_of_life <= 150:            print(year_of_life, 'Старый')            running = False    else:        print('Я вас не пойму, ведите год рождения еще раз')# a = '1-3 года - "малыш"'# b = '4-9 лет - "малыш"'# c = '10-15 лет - "подросток"'# d = '16-18 лет - "молодые"'# e = '19-50 лет - "взрослый"'# f = '51+ лет - "старый"'